                    vx = x[ix+i];
                    for(j=i+1; j<=n-1; j++)
                    {
                        v = alpha*a[ia+i,ja+j];
                        y[iy+j] = y[iy+j]+v*vx;
                        vr = vr+v*x[ix+j];
                    }
                    y[iy+i] = y[iy+i]+vr;
                }
            }
            else
            {
                
                //
                // Lower triangle of A is stored
                //
                for(i=0; i<=n-1; i++)
                {
                    
                    //
                    // Process diagonal element
                    //
                    v = alpha*a[ia+i,ja+i];
                    y[iy+i] = y[iy+i]+v*x[ix+i];
                    
                    //
                    // Process off-diagonal elements
                    //
                    vr = 0.0;
                    vx = x[ix+i];
                    for(j=0; j<=i-1; j++)
                    {
                        v = alpha*a[ia+i,ja+j];
                        y[iy+j] = y[iy+j]+v*vx;
                        vr = vr+v*x[ix+j];
                    }
                    y[iy+i] = y[iy+i]+vr;
                }
            }
        }


