            // X := X*inv(U2)
            // Y := inv(L2)*Y
            // W := inv(L1*U1)
            //
            ablas.cmatrixrighttrsm(n1, n2, a, offs+n1, offs+n1, true, false, 0, a, offs, offs+n1, _params);
